    <script>
        jQuery(function($){


            $(function(){
                /*================================================
                 ファイルをドロップした時の処理
                 =================================================*/
                $('#drag-area').bind('drop', function(e){
                    // デフォルトの挙動を停止
                    e.preventDefault();

                    // ファイル情報を取得
                    var files = e.originalEvent.dataTransfer.files;

                    uploadFiles(files);

                }).bind('dragenter', function(){
                    // デフォルトの挙動を停止
                    return false;
                }).bind('dragover', function(){
                    // デフォルトの挙動を停止
                    return false;
                });

                /*================================================
                 ダミーボタンを押した時の処理
                 =================================================*/
                $('#btn').click(function() {
                    // ダミーボタンとinput[type="file"]を連動
                    $('input[type="file"]').click();
                });

                $('input[type="file"]').change(function(){
                    // ファイル情報を取得
                    var files = this.files;


                    var file = $(this).prop('files')[0];
                    var fr = new FileReader();
                    fr.onload = function() {
                        $('#btn').attr('src', fr.result);   // 読み込んだ画像データをsrcにセット
                    }
                    fr.readAsDataURL(file);  // 画像読み込み
                    uploadFiles(files);
                });

            });

            /*================================================
             アップロード処理
             =================================================*/
            function uploadFiles(files) {
                // FormDataオブジェクトを用意
                var fd = new FormData();

                fd.append("face_photo",files['0']);
                fd.append("user_id", {{ Session::get('user.id') or 'null' }});
                fd.append("_token",$('meta[name="csrf-token"]').attr('content'));
                console.log(fd);

                // Ajaxでアップロード処理をするファイルへ内容渡す
                $.ajax({
                    url: "/user/change_face_photo",
                    type: 'POST',
                    dataType : 'json',
                    data :fd,
                    processData: false,
                    contentType: false,
                    success: function(data) {
                        console.log('ファイルがアップロードされました。');
                    },
                    error: function(XMLHttpRequest, textStatus, errorThrown){
                        console.log("エラー");
                    }
                });
            }

            $('#user_profile').click(function(){
                if(!$(this).hasClass('on')){
                    $(this).addClass('on');
                    var txt = $(this).text();
                    console.log('test');
                    $(this).text('').append('<input type="text" value="'+txt+'" />');
                    $('#user_profile > input').focus().blur(function(){
                        var inputVal = $(this).val();
                        if(inputVal===''){
                            inputVal = this.defaultValue;
                        }else {
                            $.ajax({
                                url: "/user/profile",
                                type:'POST',
                                dataType: 'json',
                                data : {
                                    _token:$('meta[name="csrf-token"]').attr('content'),
                                    id:"{{{$users->id}}}",
                                    profile:inputVal,
//                                    _token
                                },
                                timeout:10000,
                                success: function(data) {
                                    console.log('おk');
                                },
                                error: function(XMLHttpRequest, textStatus, errorThrown) {
                                    console.log("error");
                                }
                            });
                        };

                        $(this).parent().removeClass('on').text(inputVal);
                    });
                };
            });
        });
    </script>

